import '../App.css'
import principal from '../imagenes/generales/principal.jpg'
import dimenciones from '../imagenes/iconos/dimenciones.png'
import habitaciones from '../imagenes/iconos/habitaciones.png'
import nivel from '../imagenes/iconos/nivel.png'
import {useEffect,useState} from 'react'
/*cargamos las propieadades*/




function convertirBufferaImagen(buffer)
{
    console.log(buffer)

     const arrayBuffer= new Uint8Array(buffer);
     const blob= new Blob([arrayBuffer],{type:Image});
     const imagenUrl =URL.createObjectURL(blob);
     return imagenUrl;
}


export const Propiedades=()=>
{
    
    const[propiedades,setPropiedades]=useState([]);
    useEffect(
     ()=>{
         fetch("https://asesoresguzman.com/api/load")
         .then(
             response=> response.json()
         )
         .then(data=>setPropiedades(data))
         
     },[]
    )


    return(
        
        <div>
            <div className='propiedades'>
                {

                    propiedades.length >0 ?(
                    propiedades.map((propiedad,index)=>
                    (
                        <div  key={index}>
                            <div className='propiedad'>
                            <div className='contenedorImagen'>
                                <img src= {convertirBufferaImagen(propiedad.imagen.data)}/>
                            </div>
                            <div className='propiedadInfo'>
                                <div className='propiedadDetalles'>
                                    <div className='propiedadInfoTipoTVVR'>
                                        <span className='propiedadInfoTipoTerrenoVivienda'>{propiedad.tipoVentaRenta}</span>
                                        <span className='propiedadInfoTipoVentaRenta'>{propiedad.tipoTerrenoVivienda}</span>
                                    </div>
                                </div>
                                <div className='propiedadInfoDatos'>
                                    
                                </div>
                            </div>
                                
                                
                            </div>
                        </div>
                    ))
                ):(<div>No se encontraron propiedades</div>)
                }

            </div>
        </div>
    )
}
