import { useEffect, useState } from 'react';
import '../App.css';
import BackBar from './barraParaIrAtras';
import FooterLayout from './Footer';
import { PropiedadesDestacadas } from './propiedadesDestacadas';
import { useSearchParams } from 'react-router-dom';
import noEncontrado from '../imagenes/generales/no-results.png'

const  Propiedad=()=>{
    const [parametrosPropiedad]=useSearchParams();
    const filter= parametrosPropiedad.get('filter');
    const value= parametrosPropiedad.get('value');
    const[infoPropiedad,setInfoPropiedad]= useState([]);
    const url= 'https://asesoresguzman.com/api/';

    useEffect(()=>{
        fetch(url+'search?filter='+filter+'&&value='+value)
        .then(response=>response.json())
        .then(data=>{setInfoPropiedad(data)
            console.log(data);

        })
        .catch(error=>console.log(error))
    },[filter,value]);

    return(
        <div className='todo animationUp'>
            <BackBar></BackBar>
            <div>
                {
                    
                    infoPropiedad.length >0?
                    <h1>{infoPropiedad[0].nombre}
                    </h1>
                                  
                    :<span className='center'>
                        <h1>No se ha encontrado</h1>
                        <img className='notFound' src={noEncontrado}/>
                    </span>
                }
            </div>
            <FooterLayout></FooterLayout>
        </div>
    );
}
export default Propiedad;
