import '../App.css'
import {Form, Link, renderMatches, unstable_HistoryRouter} from 'react-router-dom'

export const Buscador= ()=>
{
    let valorBuscador='';
    let valorFiltro='';


    return(
        <div className='buscador'>
            <input type='text' className='buscadorInput' onChange={
                (e)=>
                {
                    valorBuscador= e.target.value;
                }
            }/>
            <select type='criterio' className='buscadorCriterio' onChange={
                (e)=>{
                    valorFiltro= e.target.value;
                }
            }>
                <option></option>
                <option>Nombre</option>
                <option>Vivienda</option>
                <option>Renta</option>
            </select>

            <input type='submit'  className='buscarBoton' value={'buscador'}
                onClick={
                    ()=>
                    {   
                        if(valorFiltro =='' || valorFiltro==null)
                        {
                            window.history.pushState('page','title','/buscar?filter=nombre&value='+valorBuscador);
                        }
                        else
                        {

                        }

                    }
                }
            />

        </div>
    )
}
