import '../App.css'
import principal from '../imagenes/generales/principal.jpg'
import { useEffect, useState } from 'react'
import inicial from '../imagenes/iconos/cohete.png'
import costo from '../imagenes/iconos/ahorrar-dinero.png'
/* Función para convertir buffer a imagen */
function convertirBufferaImagen(buffer) {
    if (buffer.length > 0) {
        const arrayBuffer = new Uint8Array(buffer);
        const blob = new Blob([arrayBuffer], { type: 'image/jpeg' });
        const imagenUrl = URL.createObjectURL(blob);
        return imagenUrl;
    } else {
        return principal;
    }
}

export const Propiedades = (props) => {
    // Estado de propiedades y qué propiedad está expandida
    const [propiedades, setPropiedades] = useState([]);
    const [detallesVisibles, setDetallesVisibles] = useState({}); // Un estado para manejar la visibilidad de cada propiedad

    // Cargar las propiedades desde la API
    function cargarPropiedades()
    {
        if (props ==0 || props ==null)
        {
            () => {
                try {
                    fetch("https://asesoresguzman.com/api/load")
                        .then(response => response.json())
                        .then(data => setPropiedades(data));
                } catch (error) {
                    console.log(error);
                }
            }
        }
        else
        {
            alert('HOLAMUNDO');
        }
    }


        useEffect(
            cargarPropiedades(), []);


    // Alternar visibilidad de detalles al hacer clic en "Ver Más"
    const cambiarVerMas = (index) => {
        setDetallesVisibles(prev => ({
            ...prev,
            [index]: !prev[index] // Cambia el estado de visibilidad para la propiedad específica
        }));
    };

    return (
        <div>
            <div className='propiedades'>
                {propiedades.length > 0 ? (
                    propiedades.map((propiedad, index) => (
                        <div key={index}>
                            <div className='propiedad'>
                                <div className='contenedorPropiedadImagen'>
                                    <img className='propiedadImagen' src={convertirBufferaImagen(propiedad.imagen.data)} alt="Propiedad" />
                                </div>
                                <div className='contenedorInfo'>
                                    <div className='contenedorEtiquetas'>
                                        <span className='contenedorTipoVentaRenta'>{propiedad.tipoVentaRenta}</span>
                                        <span className='contenedorTerrenoVivienda'>{propiedad.tipoTerrenoVivienda}</span>
                                    </div>
                                    <div className='contenedorDetalles'>
                                        <div className='contenedorDetallesPrincipal'>
                                            <span className='contenedorDetallesPrincipalNombreYCostos'>
                                                <h2>{propiedad.nombre}</h2>
                                                <span>
                                                    <img className='iconoPropiedad' title='inicial' src={inicial}/>
                                                    <h4 title='inicial'>{propiedad.inicial}</h4>
                                                    <img className='iconoPropiedad' tittle='Costo' src={costo}/>
                                                    <h4 title='costo'>{propiedad.precio}</h4>
                                                </span>

                                            </span>

                                            <span className='contenedorDetallesPrincipalMetrajes'>
                                                <h4>{propiedad.terrenoMT}</h4>
                                                <h4>{propiedad.construccionMT}</h4>
                                            </span>

                                        </div>

                                    </div>
                                </div>

                            </div>
                        </div>
                    ))
                ) : (
                    <div>No se encontraron propiedades</div>
                )}
            </div>
        </div>
    );
}
