import '../App.css'
import principal from '../imagenes/generales/principal.jpg'
import { Buscador } from './buscador'
import FooterLayout from './Footer'
import flecha from  '../imagenes/iconos/arrow-right.png'

import { Propiedades, PropiedadesDestacadas } from './propiedadesDestacadas'
export const Home=()=>
{
    let contador= 1;
    let posicion= 0;




    return(
        <div className='home'>
            <div className='principal'>
            <div className='PrincipalInfoIMG'><img className='imagenPrincipal' src={principal}></img></div>
                <div className='principalInfo'>
                    <h2 className='principalInfoTitulo'>¡Encuentra la casa de tus sueños!</h2>
                    <p className='principalInfoContenido'>¡Tu Propiedad, Nuestra Prioridad!</p>
                    <Buscador></Buscador>
                </div>
            </div>
            <section className='contenedorPropiedadPrueba'>
                <img className='flechaCarrusel flechaCarruselLeft' 
                    
                    onClick={
                        ()=>{
                            try
                            {
                            let propiedades=  document.getElementById('propiedades');
                            let propiedadesVisiblesWidth= document.getElementById('contenedorPropiedades').clientWidth;
                            let propiedadWidth= document.getElementById('propiedad').clientWidth;
                            let numeroPropiedadesVisibles= Math.floor(propiedadesVisiblesWidth/propiedadWidth);
                            let numeroPropiedades= propiedades.querySelectorAll('.propiedad').length;
                            let relacionContador= numeroPropiedades - (numeroPropiedadesVisibles-1);

                            if (contador > 0)
                            {
                                contador-=1;
                                propiedades.style.transform= 'translateX(-'+(16*contador)+'vw)';
                            }
                        }catch(error){console.log(error)}

                            
                        }
                    }
                src={flecha}/>
                <PropiedadesDestacadas></PropiedadesDestacadas>
                <img className='flechaCarrusel ' 
                    onClick={
                        ()=>{
                            
                            try
                            {
                            let propiedades=  document.getElementById('propiedades');
                            let propiedadesVisiblesWidth= document.getElementById('contenedorPropiedades').clientWidth;
                            let propiedadWidth= document.getElementById('propiedad').clientWidth;
                            let numeroPropiedadesVisibles= Math.floor(propiedadesVisiblesWidth/propiedadWidth);
                            let numeroPropiedades= propiedades.querySelectorAll('.propiedad').length;
                            let relacionContador= numeroPropiedades - (numeroPropiedadesVisibles-1);

                            if (contador < relacionContador)
                            {
                                contador+=1;
                                propiedades.style.transform= 'translateX(-'+(16*contador)+'vw)';
                            }
                        }catch(error){console.log(error);}
                            
                        }
                    }
                src={flecha}/>
            </section>
            <section className='Footer'>
                <FooterLayout></FooterLayout>
            </section>
        </div>
    )
}
